import React, {useState} from 'react'

const UserTable = ({ addUser, deleteUser, users, setUsers, updateUser, editing }) => {

    const [userName, setUserName] = useState('')
    const [userSurname, setUserSurname] = useState('')

    const handleDeleteUser = (id) => {
        let answer = window.confirm('Вы точно хотите удалить пользователя?')
        if (answer) {
            deleteUser(id)
        }
    }

    const handleSubmit = (e) => {
        e.preventDefault(e)
        addUser(userName, userSurname)
    }

    const handleChangeName = (e) => {
        setUserName(e.currentTarget.value)
    }
    const handleChangeSurname = (e) => {
        setUserSurname(e.currentTarget.value)
    }

    const updateUserData = (e, id) => {
        e.preventDefault(e)
        updateUser(id, userName, userSurname)
    }


    return <div>
        <h1>Список пользователей: {users.length}</h1>

        <table>
            <thead>
            <tr>
                <th>Name</th>
                <th>Surname</th>
                <th>Actions</th>
            </tr>
            </thead>
            <tbody>
            {users.length > 0 ? (
                users.map(user => (
                    <tr key={user._id}>
                        <td>{!user.data ? 'нет данных' : user.data.name }</td>
                        <td>{!user.data ?  'нет данных' : user.data.surname}</td>
                        <td>
                            {/*() => updateUser(user._id)*/}
                            <button type='button' className='editBtn' onClick={() => editing = true}>Edit</button>
                            <button type='button' className='button muted-button' onClick={() => handleDeleteUser(user._id)}>Delete
                            </button>
                        </td>

                    </tr>
                ))
            ) : (
                <tr>
                    <td colSpan={3}>No users</td>
                </tr>
            )}
            </tbody>
        </table>
        <form >
            <div className='labelName'>
                <label>Name </label>
                <input
                    value={userName}
                    type='text'
                    onChange={handleChangeName}
                    placeholder='Введите имя'
                />
            </div>
            <div className='labelSurname'>
                <label>Surname </label>
                <input
                    value={userSurname}
                    type='text'
                    onChange={handleChangeSurname}
                    placeholder='Введите фамилию'
                />
            </div>
            {editing ? <div><button type='submit'>Update user</button><button type='submit' onClick={() => {
                        editing = false

                        }}>Cancel</button></div>
                     :  <button type='submit' onClick={handleSubmit}>Add new user</button>}
        </form>
    </div>
}

export default UserTable
