import React, {useState} from 'react';
import './App.css';
import axios from 'axios';
import UserTable from './components/UserTable';

function App (){
    const instance = axios.create({
        baseURL: 'http://178.128.196.163:3000/api/',
    })

    const [users, setUsers] = useState([])
    const [editing, setEditing] = useState(false)
    const [currentUserName, setCurrentUserName] =  useState([])

    React.useEffect(() => {
        instance.get('records')
            .then(response => setUsers(response.data))
    }, [])

    const deleteUser = (id) => {
        console.log(editing)
        setEditing(false)
        instance.delete(`records/${id}`)
        setUsers(users.filter(user => user._id !== id))
        console.log(editing)
    }

    const addUser = (userName, userSurname) => {
        if (userName && userSurname) {
            const newUser = {
                "data": {
                    "name": userName,
                    "surname": userSurname,
                }
            }
            instance.put('records', {"data": {"name": userName, "surname": userSurname}})
                .then(() => {
                    alert('Пользователь добавлен')
                })
            setUsers([...users, newUser])

        } else {
            alert('Введите данные!')
        }
    }

    // const updateUser = (id, updatedUser) => {
    //     // когда мы готовы обновить пользователя, ставим флажок editing в false
    //     setEditing(false)
    //     // и обновляем пользователя, если нашли его по id
    //     setUsers(users.map(user => (user.id === id ? updatedUser : user)))
    // }

    // запрос на обновление данных

    const updateUser = (id, name, surname) => {
        setEditing(false)
        // instance.post(`records/${id}`, {"data": {"name": name, "surname": surname}})
        //     .then(() => alert('Данные пользователя обновлены'))
    }

    // редактирование пользователя
    // const editRow = user => {
    //     // готовы редактировать - флажок в true
    //     setEditing(true)
    //     // устанавливаем значения полей для формы редактирования
    //     // на основании выбранного "юзера"
    //     setCurrentUserName({ _id: user._id, data: {name: user.name, username: user.username })
    // }

    return (
      <div className="App">
          <UserTable setUsers={setUsers} users={users} deleteUser={deleteUser}
                     addUser={addUser} updateUser={updateUser} editing={editing} setEditing={setEditing}/>
      </div>
    )
}

export default App;
