import React from 'react';
import './App.css';
import axios from 'axios';
import UserTable from './components/UserTable';

function App (){
    const instance = axios.create({
        baseURL: 'http://178.128.196.163:3000/api/',
    })

    const [users, setUsers] = React.useState([{_id: null, data: {name: '', surname: ''}, __v: 0}])

    React.useEffect(() => {
        instance.get('records')
            .then(response => setUsers(response.data))
    }, [])

    const deleteUser = (id) => {
        instance.delete(`records/${id}`)
        setUsers(users.filter(user => user._id !== id))
    }


    let currentName = ''
    let currentSurname = ''

    let newNameText = React.createRef()
    let newSurnameText = React.createRef()

    const currentNameChange = (event) => {
        newNameText.current.value = currentName
    }

    const addUser = () => {
        setUsers(users.push(currentName))
    }


    return (
      <div className="App">
          <UserTable setUsers={setUsers} users={users} deleteUser={deleteUser} newNameText={newNameText} currentNameChange={currentNameChange} addUser={addUser} />
      </div>
    )
}

export default App;
