import React from 'react';
import './App.css';
import HomePage from './pages/HomePage/HomePage';
import axios from 'axios';

function App (){
    const instance = axios.create({
        withCredentials: true,
        baseURL: 'http://178.128.196.163:3000/api/',
    })


    const [users, setUsers] = React.useState([])

    React.useEffect(() => {
        instance.get('records')
            .then(data => setUsers(data.data))
    })

    const initialFormState = { _id: null, data: { name: '', surname: ''}, __v: 0}
    // значение "текущий пользователь на редактировании" + функция установки этого значения
    const [currentUser, setCurrentUser] = React.useState(initialFormState)

    const addUser = user => {
        user._id = users.length + 1
        setUsers([...users, user])
    }

    return (
      <div className="App">
        <HomePage users={users} addUser={addUser()}/>
      </div>
    )
}

export default App;
