import React from 'react';
import './App.css';
import axios from 'axios';
import UserTable from './components/UserTable';

function App (){
    const instance = axios.create({
        baseURL: 'http://178.128.196.163:3000/api/',
    })

    const [users, setUsers] = React.useState([{_id: null, data: {name: '', surname: ''}, __v: 0}])

    React.useEffect(() => {
        instance.get('records')
            .then(response => setUsers(response.data))
    }, [])

    const deleteUser = (id) => {
        instance.delete(`records/${id}`)
        setUsers(users.filter(user => user._id !== id))
    }

    const addUser = (userName, userSurname) => {
        if (userName && userSurname) {
            const newUser = {
                "_id": Math.random().toString(36).substr(5,12),
                "data": {
                    "name": userName,
                    "surname": userSurname,
                },
                "__v": 0
            }
            axios.put('http://178.128.196.163:3000/api/records', {})
                .then(() => {
                    setUsers([...users, newUser])
                    alert('готово')
                })

        }
    }


    return (
      <div className="App">
          <UserTable setUsers={setUsers} users={users} deleteUser={deleteUser}
                     addUser={addUser} />
      </div>
    )
}

export default App;
