import React from 'react';

const UpdateUser = (props) => {
    const [user, setUser] = React.useState(props.currentUser)

    const handleInputChange = event => {
        const { name, value } = event.target

        setUser({ ...user, [name]: value })
    }

    const handleSubmit = event => {
        event.preventDefault()
        if (!user.name || !user.username) return

        // вызываем updateUser
        props.updateUser(user.id, user)
    }

    return <div>
        <form className="form-horizontal">
            <div className="form-group">
                <input id="inputName" name="name" type="text" placeholder="Enter name"
                       className="form-control input-md"/>
                <input id="inputSurname" name="surname" type="text" placeholder="Enter surname"
                       className="form-control input-md"/>
            </div>

            <div className="buttonsAdd">
                <button id="button1id" name="button1id" className="btn btn-success">Edit user</button>
                <button id="button1id" name="button1id" className="btn btn-success">Cancel</button>
            </div>
        </form>
    </div>
}

export default UpdateUser
