import React from 'react'
import axios from 'axios';

const UserTable = (props) => {
    const [users, setUsers] = React.useState([])

    // axios.post('http://localhost:3000/users.json', {
    //         id: 3,
    //         name: 'alla',
    //         username: 'fuff'
    //     }).then(() => console.log(111))
    React.useEffect(() => {
        axios.get('http://178.128.196.163:3000/api/records')
            .then(data => setUsers(data.data))
    }, [])

    return <div>
        <table>
            <thead>
            <tr>
                <th>Name</th>
                <th>UserName</th>
                <th>Actions</th>
            </tr>
            </thead>
            <tbody>
            {users.length > 0 ? (
                users.map(user => (
                    <tr key={user._id}>
                        <td>{user.data.name}</td>
                        <td>{user.data.surname}</td>
                        <td>
                            {/*<button className='button muted-button' onClick={() => console.log('вы кликнули')}>Edit</button>*/}
                            <button className='button muted-button'>Delete</button>
                        </td>

                    </tr>
                ))
            ) : (
                <tr>
                    <td colSpan={3}>No users</td>
                </tr>
            )}

            <tr>
                <td>
                    <input type='text' placeholder='firstname' id='firstname'/>
                    <input type='text' placeholder='surname' id='surname'/>
                    <button className='button muted-button' >Add
                    </button>
                </td>
            </tr>
            </tbody>
        </table>
    </div>
}

export default UserTable
