import React from 'react'
import axios from 'axios';

const UserTable = (props) => {
    const handleDeleteUser = (id) => {
        let answer = window.confirm('Are you sure?')

        if (answer) {
            props.deleteUser(id)
        }
    }

    const func = () => {
        // axios.put('http://178.128.196.163:3000/api/records', {})

        $.ajax({
            url: 'http://178.128.196.163:3000/api/records',    //Your api url
            type: 'PUT',   //type is any HTTP method
            data: {
                data: {"_id": "610d3392ae66af078a516fa5ac1",
                        "data": {
                            "name": "Marina",
                            "surname": "Leonidovna"
                        },
                        "__v": 0}
            },      //Data as js object
            success: function () {
                console.log(props.users)
            }
        })

            .then(() => {props.setUsers(props.users.push({
                "_id": "610d3392ae66af078a516fa5ac1",
                "data": {
                    "name": "Marina",
                    "surname": "Leonidovna"
                },
                "__v": 0
            }))

                })


    }



    console.log(props.users)
    return <div>
        <table>
            <thead>
            <tr>
                <th>Name</th>
                <th>Surname</th>
                <th>Actions</th>
            </tr>
            </thead>
            <tbody>
            {props.users.length > 0 ? (
                props.users.map(user => (
                    <tr key={user._id}>
                        <td>{user.data.name}</td>
                        <td>{user.data.surname}</td>
                        <td>
                            {/*<button className='button muted-button' onClick={() => console.log('вы кликнули')}>Edit</button>*/}
                            <button type='button' className='button muted-button' onClick={() => handleDeleteUser(user._id)}>Delete
                            </button>
                        </td>

                    </tr>
                ))
            ) : (
                <tr>
                    <td colSpan={3}>No users</td>
                </tr>
            )}

            <tr>
                <td>
                    <form>
                        <label>Name</label>
                        <input
                            type='text'
                            name='name'
                            ref={props.newNameText}
                            onChange={props.currentNameChange}
                        />
                        <label>Username</label>
                        <input
                            type='text'
                            name='username'
                            // onChange={handleInputChange}
                        />
                        <button type='button' onClick={func}>Add new user</button>
                    </form>
                </td>
            </tr>
            </tbody>
        </table>
    </div>
}

export default UserTable
