import React, {useState} from 'react'
import UpdateUser from './UpdateUser';
import AddNewUser from './AddNewUser';

const UserTable = ({ addUser, deleteUser, users, setUsers, updateUser, editing, setEditing, editRow }) => {

    const [userName, setUserName] = useState('')
    const [userSurname, setUserSurname] = useState('')

    const handleDeleteUser = (id) => {
        let answer = window.confirm('Вы точно хотите удалить пользователя?')
        if (answer) {
            deleteUser(id)
        }
    }
    const handleSubmitAdd = (e) => {
        e.preventDefault(e)
        addUser(userName, userSurname)
    }
    const handleChangeName = (e) => {
        setUserName(e.currentTarget.value)
    }
    const handleChangeSurname = (e) => {
        setUserSurname(e.currentTarget.value)
    }

    return <div>
        <h1>Список пользователей: {users.length}</h1>

        <table>
            <thead>
            <tr>
                <th>Name</th>
                <th>Surname</th>
                <th>Actions</th>
            </tr>
            </thead>
            <tbody>
            {users.length > 0 ? (
                users.map(user => (
                    <tr key={user._id}>
                        <td>{!user.data ? 'нет данных' : user.data.name }</td>
                        <td>{!user.data ?  'нет данных' : user.data.surname}</td>
                        <td>
                            {/*() => updateUser(user._id)*/}
                            <button type='button' className='editBtn' onClick={() => editRow(user)}>Edit</button>
                            <button type='button' className='button muted-button' onClick={() => handleDeleteUser(user._id)}>Delete
                            </button>
                        </td>

                    </tr>
                ))
            ) : (
                <tr>
                    <td colSpan={3}>No users</td>
                </tr>
            )}
            </tbody>
        </table>
        {editing ? <UpdateUser/>  :  <AddNewUser handleSubmitAdd={handleSubmitAdd}/>}
    </div>
}

export default UserTable
