import React from 'react'
import axios from 'axios';

const UserTable = (props) => {
    const [users, setUsers] = React.useState([])

    // axios.post('http://localhost:3000/users.json', {
    //         id: 3,
    //         name: 'alla',
    //         username: 'fuff'
    //     }).then(() => console.log(111))
    React.useEffect(() => {
        setUsers(props.users)
        console.log(users)
    })
    // setUsers([{id: 3, name: 'alla', username: '1222'}])

    return <div>
        <table>
            <thead>
            <tr>
                <th>Name</th>
                <th>UserName</th>
                <th>Actions</th>
            </tr>
            </thead>
            <tbody>
            {props.users.length > 0 ? (
                props.users.map(user => (
                    <tr key={user.id}>
                        <td>{user.name}</td>
                        <td>{user.username}</td>
                        <td>
                            {/*<button className='button muted-button' onClick={() => console.log('вы кликнули')}>Edit</button>*/}
                            <button className='button muted-button'>Delete</button>
                        </td>

                    </tr>
                ))
            ) : (
                <tr>
                    <td colSpan={3}>No users</td>
                </tr>
            )}

            <tr>
                <td>
                    <input type='text'/>
                    <button className='button muted-button' onClick={() => {
                        console.log(users)
                    }}>Add
                    </button>
                </td>
            </tr>
            </tbody>
        </table>
    </div>
}

export default UserTable
